mapper.select:
SELECT
-- 级联表的字段 ,kecheng.laoshi_id as kechengLaoshiId ,kecheng.kecheng_uuid_number as kechengUuidNumber ,kecheng.kecheng_name as kechengName ,kecheng.kecheng_photo as kechengPhoto ,kecheng.kecheng_file as kechengFile ,kecheng.kecheng_video as kechengVideo ,kecheng.kemu_types as kemuTypes ,kecheng.kecheng_types as kechengTypes ,kecheng.kecheng_clicknum as kechengClicknum ,kecheng.kecheng_content as kechengContent ,kecheng.shangxia_types as shangxiaTypes ,kecheng.kecheng_delete as kechengDelete ,yonghu.yonghu_uuid_number as yonghuUuidNumber ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.banji_types as banjiTypes ,yonghu.yonghu_email as yonghuEmail
FROM kecheng_collection a left JOIN kecheng kecheng ON a.kecheng_id = kecheng.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.kecheng_id = #{params.kechengId} )
and ( a.yonghu_id = #{params.yonghuId} )
and a.kecheng_collection_types = #{params.kechengCollectionTypes}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.kecheng_id IS NOT NULL
and kecheng.laoshi_id = #{params.laoshiId}
and kecheng.kecheng_uuid_number like CONCAT('%',#{params.kechengUuidNumber},'%')
and kecheng.kecheng_name like CONCAT('%',#{params.kechengName},'%')
and kecheng.kecheng_video like CONCAT('%',#{params.kechengVideo},'%')
and kecheng.kemu_types = #{params.kemuTypes}
and kecheng.kecheng_types = #{params.kechengTypes}
and kecheng.kecheng_clicknum >= #{params.kechengClicknumStart}
and kecheng.kecheng_clicknum <= #{params.kechengClicknumEnd}
and kecheng.kecheng_clicknum = #{params.kechengClicknum}
and kecheng.kecheng_content like CONCAT('%',#{params.kechengContent},'%')
and kecheng.shangxia_types = #{params.shangxiaTypes}
and kecheng.kecheng_delete >= #{params.kechengDeleteStart}
and kecheng.kecheng_delete <= #{params.kechengDeleteEnd}
and kecheng.kecheng_delete = #{params.kechengDelete}
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_uuid_number like CONCAT('%',#{params.yonghuUuidNumber},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.banji_types = #{params.banjiTypes}
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
order by a.${params.orderBy} desc
